import React,{ useState, useEffect } from 'react';
import Button from '../componentes/button/Button';
//import { StyleSheet, css } from "aphrodite";
//import './App.css';
import firebase from '../config'
//import Item from './componentes/selectItem/Item'

/* const style = StyleSheet.create({
  btnBreakfast: {
    color:'red',
  },

}) */

//<Button text="Clique aqui" />

function Salao() {

  const [item1, setItem1] = useState([]);
  const [item2, setItem2] = useState([]);
  const [productSelect, setProductSelect] =useState([]);

  useEffect(() => {

    firebase.collection('Menu')
      .get().then((snapshot) => {
        const products = snapshot.docs.filter(doc => doc.breakfast).map((doc)=>({
          id: doc.id,
          ...doc.data()
        }))
        setItem1(products)

        const products2 = snapshot.docs.filter(doc => doc.lunch).map((doc)=>({
          id: doc.id,
          ...doc.data()
        }))
        setItem2(products)

    })

    firebase.collection('Menu')
    .where('breakfast','==', true)
      .get().then((snapshot) => {
        const products = snapshot.docs.map((doc)=>({
          id: doc.id,
          ...doc.data()
        }))
        setItem1(products)
    })
    firebase.collection('Menu')
    .where('lunch','==', true)
    .get().then((snapshot) => {
        const products = snapshot.docs.map((doc)=>({
          id: doc.id,
          ...doc.data()
        }))
        setItem2(products)
    })
  }, [])


  const funcao = (item) => {
    setProductSelect([...productSelect, item])
  }

  return (
    <div className="App">
      <header className="App-header">

      <div>
      <p>Breakfast </p>
        {item1.map((teste) => <Button handleClick={() => funcao(teste)} name={teste.name} price={teste.price} key={teste.id}/> )}
      </div>
      <div>
      <p>All Day </p>
        {item2.map((teste) => <Button name={teste.name} price={teste.price} key={teste.id}/> )}
      </div>
      {
        productSelect.map(product => <div>{product.name}</div>)
      }




      {/*  { <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>} */}
      </header>
    </div>
  );
} 


export default Salao;
