import React,{ useState, useEffect } from 'react';
import Button from '../componentes/button/Button';
import MenuList from '../componentes/menu/Menu';
import firebase from '../utils/config'
import { StyleSheet, css } from "aphrodite";
import Input from '../componentes/input/Input';
//import { tsPropertySignature } from '@babel/types';
//import Item from './componentes/selectItem/Item'

const style = StyleSheet.create({
  disInline: {
    display:'inline-block',
    backgroundColor:'red',
  },

})

//<Button text="Clique aqui" />

function Restaurante() {

  const [item1, setItem1] = useState([]);
  const [item2, setItem2] = useState([]);
  const [productSelect, setProductSelect] = useState([]);
  const [filterMenu, setFilterMenu] = useState("breakfast");
  const [client, setClient] = useState ([]);
  const [table,setTable] = useState('');
  const [total,setTotal] = useState('');

  useEffect(() => {

    firebase.collection('Menu')
      .get().then((snapshot) => {
        const products = snapshot.docs.filter(doc => doc.data().breakfast).map((doc)=>({
          id: doc.id,
          ...doc.data()
        }))
        setItem1(products)

        const products2 = snapshot.docs.filter(doc => doc.data().lunch).map((doc)=>({
          id: doc.id,
          ...doc.data()
        }))
        setItem2(products2)

    })
  }, [])

  function onSubmit(e){
      e.preventDefault()

      firebase.firestore
      .collection('client')
      .add({
        client,
        table: parseInt(table),
        productSelect,
        total,
      })
      .then(() => {
        setTable('')
        setClient('')
        setProductSelect([])
        setTotal('')
      })
  }

  const addOrder = (item) =>{
    if(!productSelect.includes(item)) {
        item.contador = 1;
        setProductSelect([...productSelect, item])
    } else {
        item.contador += 1;
        setProductSelect([...productSelect])             
       }   
}

  const valorPedido = productSelect.reduce((acc, item) => acc + ( item.contador * item.price), 0);

  const remove = (item) => {
    const index = (productSelect.indexOf(item));
    productSelect.splice(index, 1);
    setProductSelect([...productSelect]);
  }

  return (
   <>
    <section className="App">
      <header className="App-header"></header>
        <div>
         <Button text={'Breakfast'} handleClick={() => setFilterMenu('breakfast') } />
         <Button text={'All Day'} handleClick={() => setFilterMenu('lunch') } />
        </div>
        <div>
         <h2>Menu</h2>
          <div className={css(style.disInline)}>
            <MenuList 
              menuItens={filterMenu === "breakfast" ? item1 : item2} 
              handleClick={addOrder} 
              name={productSelect.name} 
              price={productSelect.price} key={productSelect.id}/>
        </div>
      </div> 
    </section>

    <section className={css(style.disInline)}>
      <div>
          <label>
           <strong>Cliente</strong>
          </label>
         <Input id='input-client' type="text" state={client} handleChange={e => setClient(e.currentTarget.value)}/>

       <label>
         <strong>Mesa</strong>
       </label>

         <Input id='input-number' type="number" state={table} handleChange={e => setTable(e.currentTarget.value)}/>
      </div>

          {productSelect.map((product, index) => (
              <div key={index}> 
               {product.contador}
               {product.name} R$ {product.price},00 
                <Button text={'Del'} handleClick={(e) => {
                  e.preventDefault(); 
                  remove(product);
                }} />
              </div>   
          ))}


       <p><strong>Total: {valorPedido}</strong></p>
       <Button id='button' handleClick={onSubmit} text={'Enviar'}/> 

    </section>
  </>
  );
};

export default Restaurante;
