import React,{ useState, useEffect } from 'react';
import Card from '../componentes/card/Card';
import Button from '../componentes/button/Button';
import MenuList from '../componentes/menu/Menu';
//import { StyleSheet, css } from "aphrodite";
//import './App.css';
import firebase from '../config'
//import Item from './componentes/selectItem/Item'

/* const style = StyleSheet.create({
  btnBreakfast: {
    color:'red',
  },

}) */

//<Button text="Clique aqui" />

function Salao() {

  const [item1, setItem1] = useState([]);
  const [item2, setItem2] = useState([]);
  const [productSelect, setProductSelect] = useState([]);
  const [filterMenu, setFilterMenu] = useState([]);

  useEffect(() => {

    firebase.collection('Menu')
      .get().then((snapshot) => {
        const products = snapshot.docs.filter(doc => doc.data().breakfast).map((doc)=>({
          id: doc.id,
          ...doc.data()
        }))
        console.log(products)
        setItem1(products)

        const products2 = snapshot.docs.filter(doc => doc.data().lunch).map((doc)=>({
          id: doc.id,
          ...doc.data()
        }))
        setItem2(products2)

    })

  }, [])

  function showFilterMenu (type) {
    if(type === 'breakfast'){
      const filterAdd = item1.filter(elem => elem.breakfast);
      setFilterMenu(filterAdd);
    }else{
      const filterAdd = item2.filter(elem => !elem.breakfast);
      setFilterMenu(filterAdd);
    }
  }



  const addOrder = (item) => {
    setProductSelect([...productSelect, item])
  }



  return (
    <div className="App">
      <header className="App-header">

      <div>
      <Button text={'Breakfast'} handleClick={() => showFilterMenu('breakfast') } />
      <Button text={'All Day'} handleClick={() => showFilterMenu('lunch') } />


        {item1.map((teste) => <Card handleClick={() => addOrder(teste)} name={teste.name} price={teste.price} key={teste.id}/> )}
      </div>
      <div>

        {item2.map((teste) => <Card handleClick={() => addOrder(teste)} name={teste.name} price={teste.price} key={teste.id}/> )}
      </div>
      {
        productSelect.map(product => <div>{product.name} R$ {product.price},00
        </div>)
      }

      <div>
        <h2>Menu</h2>
        < Card menuItens={filterMenu}/>
      </div>


      {/*  { <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>} */}
      </header>
    </div>
  );
} 


export default Salao;
