import React,{ useState, useEffect } from 'react';
//import Button from '../src/componentes/Button';
//import { StyleSheet, css } from "aphrodite";
//import './App.css';
import firebase from './config'

/* const style = StyleSheet.create({
  btnBreakfast: {
    color:'red',
  },

}) */

//<Button text="Clique aqui" ></Button>

function App() {

  const [item1, setItem1] = useState([]);

  useEffect(() => {
    firebase.collection('Menu')
    .where('breakfast','==', true)
      .onSnapshot((snapshot) => {
        const products = snapshot.docs.map((doc)=>({
          id: doc.id,
          ...doc.data()
        }))
        setItem1(products)
    })
  }, [])

  const [item2, setItem2] = useState([]);

  useEffect(() => {
    firebase.collection('Menu')
    .where('lunch','==', true)
      .onSnapshot((snapshot) => {
        const products = snapshot.docs.map((doc)=>({
          id: doc.id,
          ...doc.data()
        }))
        setItem2(products)
    })
  }, [])

  return (
    <div className="App">
      <header className="App-header">
        <p>
          {item1.map((teste) =>
          <div key = {teste.id}>
            {teste.name}
            {"R$" + teste.price + ",00"}
          </div>
          )}
        </p>
        <p>
          {item2.map((teste) =>
          <div key = {teste.id}>
            {teste.name}
            {"R$" + teste.price + ",00"}
          </div>
          )}
        </p>

       {/*  <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a> */}
      </header>
    </div>
  );
} 


export default App;
