import React,{ useState, useEffect } from 'react';
import Button from './componentes/button/Button';
//import { StyleSheet, css } from "aphrodite";
//import './App.css';
import firebase from './config'
import Item from './componentes/selectItem/Item'

/* const style = StyleSheet.create({
  btnBreakfast: {
    color:'red',
  },

}) */

//<Button text="Clique aqui" />

function App() {

  const [item1, setItem1] = useState([]);
  const [item2, setItem2] = useState([]);

  useEffect(() => {
    firebase.collection('Menu')
    .where('breakfast','==', true)
      .onSnapshot((snapshot) => {
        const products = snapshot.docs.map((doc)=>({
          id: doc.id,
          ...doc.data()
        }))
        setItem1(products)
    })
  }, [])

  useEffect(() => {
    firebase.collection('Menu')
    .where('lunch','==', true)
      .onSnapshot((snapshot) => {
        const products = snapshot.docs.map((doc)=>({
          id: doc.id,
          ...doc.data()
        }))
        setItem2(products)
    })
  }, [])

  return (
    <div className="App">
      <header className="App-header">

      <div>
      <p>Breakfast </p>
        {item1.map((teste) => <Item name={teste.name} price={teste.price} key={teste.id}/> )}
      </div>
      <div>
      <p>All Day </p>
        {item2.map((teste) => <Item name={teste.name} price={teste.price} key={teste.id}/> )}
      </div>




       {/*  <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a> */}
      </header>
    </div>
  );
} 


export default App;
